    return null;
}
pub fn triangleUniformOffset(ub_name: []const u8, u_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "fs_params")) {
        if (std.mem.eql(u8, u_name, "model")) {
            return 0;
        }
        if (std.mem.eql(u8, u_name, "view")) {
            return 64;
        }
        if (std.mem.eql(u8, u_name, "projection")) {
            return 128;
        }
    }
    return null;
}
pub fn triangleUniformDesc(ub_name: []const u8, u_name: []const u8) ?sg.GlslShaderUniform {
    if (std.mem.eql(u8, ub_name, "fs_params")) {
        if (std.mem.eql(u8, u_name, "model")) {
            var desc: sg.GlslShaderUniform = .{};
            desc.type = .MAT4;
            desc.array_count = 0;
            desc.glsl_name = "model";
            return desc;
        }
        if (std.mem.eql(u8, u_name, "view")) {
            var desc: sg.GlslShaderUniform = .{};
            desc.type = .MAT4;
            desc.array_count = 0;
            desc.glsl_name = "view";
            return desc;
        }
        if (std.mem.eql(u8, u_name, "projection")) {
            var desc: sg.GlslShaderUniform = .{};
            desc.type = .MAT4;
            desc.array_count = 0;
            desc.glsl_name = "projection";
            return desc;
        }
    }
    return null;
}
